;; taken from day 1
(defn two-sum [target xs]
  (let [res (reduce (fn [seen x]
                      (let [rest (- target x)]
                        (if (seen rest)
                          (reduced [rest x])
                          (conj seen x))))
                    #{}
                    xs)]
    (if (vector? res)
      res
      nil)))

(defn chunks [n xs]
  (let [windows (partition n 1 xs)
        targets (drop n xs)]
    (map vector targets windows)))

(defn contiguous-windows [xs]
  (let [chunks (take (count xs)
                     (map #(drop %1 xs) (range)))]
    (mapcat #(reductions conj [] %) chunks)))

(defn contiguous-sum [target xs]
  (letfn [(csum [xs [n & more :as bank]]
            (let [s (apply + xs)]
              (cond
                (= s target) xs
                (nil? n) nil
                (< s target) (csum (conj xs n) more)
                (> s target) (csum (vec (rest xs))
                                   bank))))]
    (csum [] xs)))

;; A
(->> (chunks 25 input)
     (map #(vector (apply two-sum %) (first %)))
     (some #(when (not (first %)) %))
     (second))

;; B brute force
(time (->> (contiguous-windows input)
           (some #(when (= 466456641 (apply + %)) %))
           ((juxt #(apply min %) #(apply max %)))
           (apply +)))

;; B efficient
(time (->> (contiguous-sum 466456641 input)
           ((juxt #(apply min %) #(apply max %)))
           (apply +)))





